import React from "react";
import { Dimensions } from "react-native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { createStackNavigator } from "@react-navigation/stack";
import { nowTheme } from "../constants";

import { HomeStack } from "./StackHome";
import { LeaveManagementStack } from "./StackLeaveManagement";
import { BranchStack } from "./StackBranch";
import { DesignationManagementStack } from "./StackDesignationManagement";
import { StaffManagementStack } from "./StackStaffManagement";
import { LoanManagementStack } from "./StackLoanManagement";

const { width } = Dimensions.get("screen");

export const Stack = createStackNavigator();
export const Drawer = createDrawerNavigator();

function AppStack(props) {
    return (
      <Drawer.Navigator
        style={{ flex: 1 }}
        drawerContent={(props) => <CustomDrawerContent {...props} />}
        drawerStyle={{
          backgroundColor: nowTheme.COLORS.PRIMARY,
          width: width * 0.8,
        }}
        drawerContentOptions={{
          activeTintcolor: nowTheme.COLORS.WHITE,
          inactiveTintColor: nowTheme.COLORS.WHITE,
          activeBackgroundColor: "transparent",
          itemStyle: {
            width: width * 0.75,
            backgroundColor: "transparent",
            paddingVertical: 16,
            paddingHorizonal: 12,
            justifyContent: "center",
            alignContent: "center",
            alignItems: "center",
            overflow: "hidden",
          },
          labelStyle: {
            fontSize: 18,
            marginLeft: 12,
            fontWeight: "normal",
          },
        }}
        initialRouteName="Home"
      >
        <Drawer.Screen name="Home" component={HomeStack} />
        <Drawer.Screen name="Leaves Management" component={LeaveManagementStack} />
        <Drawer.Screen name="Branch Management" component={BranchStack} />
        <Drawer.Screen name="Designation Management" component={DesignationManagementStack} />
        <Drawer.Screen name="Staff Management" component={StaffManagementStack} />
        <Drawer.Screen name="Loan Management" component={LoanManagementStack} />
        <Drawer.Screen name="Settings" component={SettingsStack} />
      </Drawer.Navigator>
    );
}

export default function OnboardingStack(props) {
    return (
      <Stack.Navigator
        screenOptions={{
          mode: "card",
          headerShown: false,
        }}
      >
        <Stack.Screen
          name="Onboarding"
          component={Pro}
          option={{
            headerTransparent: true,
          }}
        />
        <Stack.Screen name="App" component={AppStack} />
      </Stack.Navigator>
    );
}
